// src/pages/Collecte.jsx
import { useState } from "react";

export default function Collecte() {
  const [formData, setFormData] = useState({
    co2: "",
    eau: "",
    accidents: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Données sauvegardées (mock) : " + JSON.stringify(formData));
  };

  return (
    <div>
      <h1 className="text-xl font-bold mb-4">Collecte des données ESG</h1>
      <form onSubmit={handleSubmit} className="space-y-3">
        <input
          type="number"
          name="co2"
          placeholder="Émissions CO₂ (tonnes)"
          value={formData.co2}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />
        <input
          type="number"
          name="eau"
          placeholder="Consommation d’eau (m³)"
          value={formData.eau}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />
        <input
          type="number"
          name="accidents"
          placeholder="Accidents de travail"
          value={formData.accidents}
          onChange={handleChange}
          className="border p-2 rounded w-full"
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Sauvegarder
        </button>
      </form>
    </div>
  );
}
